import React, { useState } from "react";

function Result() {
  return (
    <div className="result">
      <img></img>
      <div>your result is 3 of 10</div>
      <button> tray agan</button>
    </div>
  );
}
const questions = [
  {
    title: "react - it..",
    variants: ["freemwork", "biblioteca", "application"],
    correct: 1,
    id: 1,
  },
  {
    title: "compontnts - it..",
    variants: ["freemwork", "i'm good", "biblioteca"],
    correct: 2,
    id: 2,
  },
  {
    title: "JSX - it...",
    variants: ["freemwork", "i'm good", "freemwork"],
    correct: 1,
    id: 3,
  },
];

function Game({ stap, question, onclickVariant }) {
  const procant = ((stap + 1) / questions.length) * 100;
  console.log(procant);
  return (
    <div className="game">
      <div className="progress-bar">
        <div className="progress" style={{ width: `${procant}%` }}></div>
      </div>
      <h1>{question.title}</h1>
      <ul>
        {question.variants.map((text, index) => (
          <li onClick={() => onclickVariant(index)} key={index}>
            {text}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [step, setStep] = React.useState(0);
  const question = questions[step];

  const onclickVariant = (index) => {
    console.log(step, index);
    setStep(step + 1);
  };

  return (
    <div className="app">
      <div className="content">
        <Game step={step} question={question} onclickVariant={onclickVariant} />
        {
          // <Result/>
        }
      </div>
    </div>
  );
}

export default App;
