import React, { useState } from "react";

function Game({ question, onAnswer }) {
  return (
    <div className="game">
      <div className="progress-bar">
        <div className="progress" style={{ width: "33.33%" }}></div>
      </div>
      <h1>{question.title}</h1>
      <ul>
        {question.variants.map((text, index) => (
          <li key={index} onClick={() => onAnswer(question.id, index)}>
            {text}
          </li>
        ))}
      </ul>
    </div>
  );
}

function App() {
  const [step, setStep] = React.useState(0);
  const question = questions[step];

  const handleAnswer = (questionId, chosenAnswerIndex) => {
    console.log(
      `Answered question id: ${questionId}, chosen answer: ${chosenAnswerIndex}`
    );
    // Обновление step для отображения следующего вопроса
    setStep((prevStep) => prevStep + 1);
  };

  return (
    <div className="app">
      <div className="content">
        <Game question={question} onAnswer={handleAnswer} />
        {
          // <Result/>
        }
      </div>
    </div>
  );
}

export default App;
