import React, { useState, useEffect } from "react";
import User from "./User";

function Invit() {
  const [serchVlue, setSerchValue] = React.useState("");

  useEffect(() => {
    fetch("https://reqres.in/api/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((json) => {
        setUsers(json.data);
      })
      .catch((err) => {
        console.warn(err);
        alert("An error occurred");
      });
  }, []);

  const onChengeSerchVlue = (event) => {
    setSerchValue(event.target.value);
  };

  return (
    <div className="invit">
      <div className="invit_content">
        <form className="form">
          <input
            value={serchVlue}
            type="text"
            className="input"
            placeholder="find user..."
          ></input>
        </form>
        {users.map((user) => (
          <User
            onChengeSerchVlue={onChengeSerchVlue}
            serchVlue={serchVlue}
            {...user}
          />
        ))}
        <button>send offer</button>
      </div>
    </div>
  );
}

export default Invit;
const [users, setUsers] = React.useState([]);
