import React, { useState, useEffect } from "react";

import imageMon1 from "../Components/imges/pexels-alfonso-escalante-2533092.jpg";
import imageMon2 from "../Components/imges/pexels-chevanon-photography-333525.jpg";
import imageMon3 from "../Components/imges/pexels-dominika-roseclay-1252500.jpg";
