          "https://images.pexels.com/photos/1525612/pexels-photo-1525612.jpeg?auto=compress&cs=tinysrgb&w=600",
        ],
      },
    ],
  },
];
function Collections({ name, images }) {
  return (
    <>
      <div className="gallery">
        <div className="colection_big">
          <img src={images[0]} alt={name} />
        </div>
        <div className="colection_mini">
          {images.slice(1).map((img, index) => (
            <img key={index} src={img} alt={`${name} ${index}`} />
          ))}
        </div>
        <h1 className="name">{name}</h1>
      </div>
    </>
  );
}

const Paginations = ({ itemsPerPage, totalItems, paginate }) => {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav>
      <ul className="pagination">
        {pageNumbers.map((number) => (
          <li key={number} className="page-item">
            <button onClick={() => paginate(number)} className="page-link">
              {number}
            </button>
          </li>
        ))}
      </ul>
    </nav>
  );
};

function Galere(photo) {
  const [cotegoriId, setCotegoriId] = React.useState(0);
  const [searchValue, setSearchValue] = React.useState("");
  const [activeCategory, setActiveCategory] = React.useState("all");

  const [photos, setPhotos] = useState([]);
  const [currentPage, setCurrentPage] = useState(3);
  const itemsPerPage = 3;
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = Components[0].collections.slice(
    indexOfFirstItem,
    indexOfLastItem
  );
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const categoryToId = {
    all: 0,
    mountains: 1,
    "the sea": 2,
    architecture: 3,
    "the city": 4,
  };
  const setActiveCategoryHandler = (categoryName) => {
    setActiveCategory(categoryName);
  };
  const categories = Components[0].categories.map((category) => (
    <li
      className={activeCategory === category.name ? "active" : ""}
      key={category.name}
      onClick={() => setActiveCategoryHandler(category.name)}
    >
      {category.name}
    </li>
  ));
  const page = `pages= ${cotegoriId}`;
  const showImg = (photo) => {};
  return (
    <div className="galere">
      <h1>My photo collection </h1>
      <div className="top">
        <ul className="tahs">
          {categories}
          <input
            onChange={(e) => {
              setSearchValue(e.target.value);
            }}
            value={searchValue}
            placeholder="find"
          />
        </ul>
      </div>
      <div className="elements">
        {currentItems
          .filter(
            (obj) =>
              (activeCategory === "all" ||
                categoryToId[activeCategory] === obj.category) &&
              obj.name.toLowerCase().includes(searchValue.toLowerCase())
          )
          .map((obj, index) => (
            <Collections key={index} name={obj.name} images={obj.photo} />
          ))}
      </div>
      <div className="pages">
        <Paginations
          itemsPerPage={itemsPerPage}
          totalItems={Components[0].collections.length}
          paginate={paginate}
        />
      </div>
    </div>
  );
}

export default Galere;
