import React, { useState, useEffect } from "react";
import User from "./User";

function Invit() {
  const [searchValue, setSearchValue] = React.useState(""); // Правильное имя переменной
  const [users, setUsers] = React.useState([]);

  const onChangeValue = (event) => {
    setSearchValue(event.target.value); // Правильное имя функции
  };

  useEffect(() => {
    fetch("https://reqres.in/api/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((json) => {
        setUsers(json.data);
      })
      .catch((err) => {
        console.warn(err);
        alert("An error occurred");
      });
  }, []);

  return (
    <div className="invit">
      <div className="invit_content">
        <form className="form">
          <input
            onChange={onChangeValue}
            value={searchValue}
            type="text"
            className="input"
            placeholder="find user..."
          ></input>
        </form>
        {users.map((user) => (
          <User
            key={user.id}
            id={user.id}
            email={user.email}
            first_name={user.first_name}
            last_name={user.last_name}
            avatar={user.avatar}
          />
        ))}
        <button>send offer</button>
      </div>
    </div>
  );
}

export default Invit;
