import imageMon1 from "../Components/imges/pexels-alfonso-escalante-2533092.jpg";
import imageMon2 from "../Components/imges/pexels-chevanon-photography-333525.jpg";
import imageMon3 from "../Components/imges/pexels-dominika-roseclay-1252500.jpg";
import imageMon4 from "../Components/imges/pexels-element-digital-1051075.jpg";

const trevel = "";
const Components = [
  {
    categories: [
      { name: "all" },
      { name: "mountains" },
      { name: "the sea" },
      { name: "architecture" },
      { name: "the city" },
    ],
    collections: [
      {
        category: 1,
        name: "mountain hiking",
        photo: [
          "https://images.pexels.com/photos/9754/mountains-clouds-forest-fog.jpg?auto=compress&cs=tinysrgb&w=600",
          "https://images.pexels.com/photos/1666012/pexels-photo-1666012.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1s",
