export const Users = ({ items }) => {
  return (
    <div>
      {items.map((user, index) => (
        <div key={index} className="person">
          <img
            className="avatar"
            src={user.avatar} // Предполагаем, что каждый user имеет свойство avatar
            alt="User Avatar"
          />
          <div className="info">
            <h1>{user.name}</h1>{" "}
            {/* Предполагаем, что каждый user имеет свойство name */}
            <p>{user.email}</p>{" "}
            {/* Предполагаем, что каждый user имеет свойство email */}
          </div>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24"
            viewBox="0 -960 960 960"
            width="26"
          >
            <path d="M440-440H200v-80h240v-240h80v240h240v80H520v240h-80v-240Z" />
          </svg>
        </div>
      ))}
    </div>
  );
};

function Invit() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetch("https://reqres.in/api/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((json) => {
        setUsers(json.data);
      })
      .catch((err) => {
        console.warn(err);
        alert("An error occurred");
      });
  }, []);
  return (
    <div className="invit">
      <div className="invit_content">
        <form className="form">
          <input className="input" placeholder="find user..."></input>{" "}
        </form>
        <Users items={[]} />
        <button>send offer</button>
      </div>
    </div>
  );
}

export default Invit;
