            onChange={onChangeSearchValue}
            type="text"
            className="input"
            placeholder="find user..."
          ></input>
        </form>
        {users.map((user) => (
          <User serchVlue={serchVlue} {...user} />
        ))}
        <button>send offer</button>
      </div>
    </div>
  );
}

export default Invit;
