import React, { useState, useEffect } from "react";
import User from "./User";

function Invit() {
  const [users, setUsers] = useState([]);
  const [searchValue, setSearchValue] = useState("");
  const [invites, setInvites] = useState([]);

  const toggleInvite = (userId) => {
    if (invites.includes(userId)) {
      setInvites(invites.filter((id) => id !== userId));
    } else {
      setInvites([...invites, userId]);
    }
  };
  useEffect(() => {
    fetch("https://reqres.in/api/users")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((json) => {
        setUsers(json.data);
      })
      .catch((err) => {
        console.warn(err);
        alert("An error occurred");
      });
  }, []);

  const onChangeValue = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <div className="invit">
      <div className="invit_content">
        {/* ... форма поиска ... */}
        {users.filter(/* ... фильтрация пользователей ... */).map((user) => (
          <User
            key={user.id}
            isInvited={invites.includes(user.id)}
            onToggleInvite={() => toggleInvite(user.id)}
            {...user}
          />
        ))}
        <button>send offer</button>
      </div>
    </div>
  );
}

export default Invit;
